mapper.insert:
INSERT INTO
TB_Course_Student (
courseId,classId,userId,type,status,semesterId
)VALUES(
#{courseId},#{classId},#{userId},#{type},#{status},#{semesterId}
)

	SELECT
	LAST_INSERT_ID() AS VALUE

mapper.select:
SELECT *
from
TB_Course_Student
WHERE userId=#{userId} and type=#{type}
and status=0 and semesterId=#{semesterId}

	limit #{limit} offset #{offset};

order by id
desc;
