mapper.insert:
INSERT INTO TB_School_Info_Join
(
infoId,userId,createTime
)VALUES(
#{infoId},#{userId},#{createTime}
)

	SELECT
	LAST_INSERT_ID() AS VALUE

mapper.select:
SELECT *
from
TB_School_Info_Join
WHERE infoId=#{infoId} order by id desc

	limit #{limit} offset #{offset};

;

SELECT *
from
TB_School_Info_Join
WHERE infoId=#{infoId} and userId=#{userId}
;

mapper.delete:
delete from TB_School_Info_Join
WHERE infoId=#{infoId}
and userId=#{userId};
